package week1.day2;

public class LearnForLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int number=10;
		for (int i = 0; i < number; i++) {
			System.out.println("value :"+i);
		}
		
		
		//5 4 3 2 1 0
		//i--
		
		int a=5;
		
		for (int i = a; i>=0; i--) {
			System.out.println(i);
		}
	}
}
